import React, { useEffect, useState, useRef } from "react";
import io from "socket.io-client";
import Typist from 'react-typist';
import { ToastContainer, toast } from 'react-toastify';
import { Button, Input, Badge, Tooltip, Avatar } from 'antd';
import { LikeOutlined, DislikeOutlined } from '@ant-design/icons';
import 'react-toastify/dist/ReactToastify.css';
import './Chat.css';

const socket = io('http://localhost:4000');

const Chat = () => {
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const messagesRef = useRef(null);

  const sendMessage = (event) => {
    event.preventDefault();
    const timestamp = new Date();
    const newMessage = { text: message, avatar: "https://via.placeholder.com/40", timestamp, likes: 0, dislikes: 0, sent: true };

    // Add message to chat first
    setChat([...chat, newMessage]);

    // Then emit the event to the server
    socket.emit("chat message", newMessage);

    setMessage("");
  };

  const notify = (msg) => {
    toast(msg);
  };

  useEffect(() => {
    socket.on("chat message", (msg) => {
      setChat((chat) => [...chat, { ...msg, sent: false }]);
      notify(msg.text);
    });

    return () => socket.off("chat message");
  }, []);

  useEffect(() => {
    if (messagesRef.current) {
      messagesRef.current.scrollTop = messagesRef.current.scrollHeight;
    }
  }, [chat]);

  return (
    <div className="chat-container">
      <ToastContainer />
      <div className="messages" ref={messagesRef}>
        {chat.map((msg, index) => (
          <div className={msg.sent ? "message sent-message" : "message"} key={index}>
            <Avatar src={msg.avatar} />
            {msg.sent ? (
              <p>{msg.text}</p>
            ) : (
              <Typist className="typist">{msg.text}</Typist>
            )}
            <span>{new Date(msg.timestamp).toLocaleTimeString()}</span>
            <div>
              <Tooltip title="Like">
                <Badge count={msg.likes} overflowCount={999}>
                  <Button shape="circle" icon={<LikeOutlined />} />
                </Badge>
              </Tooltip>
              <Tooltip title="Dislike">
                <Badge count={msg.dislikes} overflowCount={999}>
                  <Button shape="circle" icon={<DislikeOutlined />} />
                </Badge>
              </Tooltip>
            </div>
          </div>
        ))}
      </div>
      <form onSubmit={sendMessage} className="send-message-form">
        <Input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type a message"
        />
        <Button type="primary" htmlType="submit">
          Send
        </Button>
      </form>
    </div>
  );
};

export default Chat;
